    public static ArrayList<StockLoanRemainView> parseData() throws IOException, InterruptedException {
        ArrayList<StockLoanRemainView> views = new ArrayList<StockLoanRemainView>();

        JsonReader reader = new JsonReader(new StringReader(result));
        reader.setLenient(true);
        reader.beginObject();

        while (reader.hasNext()) {
            String name = reader.nextName();
            if (!"data".equals(name)) {
                reader.skipValue();
                continue;
            }

            reader.beginArray();
            while (reader.hasNext()) {
                reader.beginArray();
                StockLoanRemainView view = new StockLoanRemainView();

                int index = 0;
                while (reader.hasNext()) {
                    switch (index) {
                        case INDEX_ID:
                            String id = reader.nextString();
                            view.setId(id);
                            break;
                        case INDEX_NAME:
                            String tname = reader.nextString();
                            view.setName(tname);
                            reader.nextString();
                            reader.nextString();
                            reader.nextString();
                            reader.nextString();
                            reader.nextString();
                            reader.nextString();
                            break;
                        case INDEX_BUY:
                            String buy = reader.nextString();
                            buy = buy.replace(",", "");
                            view.setBuy(Integer.valueOf(buy));
                            break;
                        case INDEX_SELL:
                            String sell = reader.nextString();
                            sell = sell.replace(",", "");
                            view.setSell(Integer.valueOf(sell));
                            break;
                        case INDEX_GIVE:
                            String give = reader.nextString();
                            give = give.replace(",", "");
                            view.setGive(Integer.valueOf(give));
                            break;
                        case INDEX_PRE_BALANCE:
                            String preBalance = reader.nextString();
                            preBalance = preBalance.replace(",", "");
                            view.setPre_balance(Integer.valueOf(preBalance));
                            break;
                        case INDEX_NOW_BALANCE:
                            String nowBalance = reader.nextString();
                            nowBalance = nowBalance.replace(",", "");
                            view.setNow_balance(Integer.valueOf(nowBalance));
                            break;
                        case INDEX_LIMIT:
                            String limit = reader.nextString();
                            limit = limit.replace(",", "");
                            view.setLimit(Integer.valueOf(limit));
                            reader.nextString();
                            reader.nextString();
                            break;
                    }
                    index++;
//                    Thread.sleep(1000);
                }
                views.add(view);
                reader.endArray();
            }
            reader.endArray();
        }

        return views;
    }

    private static void insert(Connection conn, StockLoanRemainView view) throws SQLException {
        String sql = "INSERT INTO stockloanremain (id,date,name,buy,sell,give,pre_balance,now_balance,total) VALUES (?,'108.04.12',?,?,?,?,?,?,?)";

        PreparedStatement pstmt = null;
        try {
            pstmt = conn.prepareStatement(sql);
            pstmt.setString(1, view.getId());
            pstmt.setString(2, view.getName());
            pstmt.setInt(3, view.getBuy());
            pstmt.setInt(4, view.getSell());
            pstmt.setInt(5, view.getGive());
            pstmt.setInt(6, view.getPre_balance());
            pstmt.setInt(7, view.getNow_balance());
            pstmt.setInt(8, view.getLimit());
            pstmt.executeUpdate();
            pstmt.close();

        } catch (SQLException e) {
            e.printStackTrace();
            if (1062 == e.getErrorCode()) {
                return;
            }

            throw e;
        }
    }

